import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const EditInfo = () => {
  const navigate = useNavigate();

  const [user, setUser] = useState({
    user_idemail: "",
    user_password: "",
    user_name: "",
    user_phonenumber: "",
    user_nickname: "",
    user_profile: null,
  });

  const {
    user_idemail,
    user_password,
    user_name,
    user_phonenumber,
    user_nickname,
    user_profile,
  } = user;

  const config = {
    headers: {
      "Content-Type": "multipart/form-data",
      Authorization: localStorage.getItem("Authorization"),
      "Authorization-refresh": localStorage.getItem("Authorization-refresh"),
    },
  };

  const handleValueChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const [passwordCheck, setPasswordCheck] = useState("");

  const passChang = (e) => {
    if (user_password !== e.target.value) setPasswordCheck("비밀번호 불일치");
    else setPasswordCheck("비밀번호 일치");
  };

  const info = async () => {
    try {
      const response = await axios.get(`/user/${localStorage.user_idemail}`);
      setUser((prev) => ({ ...prev, ...response.data, user_password: "" }));
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    info();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!user_password) {
      alert("비밀번호를 입력하세요.");
      return;
    }
    try {
      const formData = new FormData();
      formData.append("user_profile", user_profile);
      formData.append("user_idemail", user_idemail);
      formData.append("user_password", user_password);
      formData.append("user_name", user_name);
      formData.append("user_phonenumber", user_phonenumber);
      formData.append("user_nickname", user_nickname);

      await axios.put(`/user/update`, formData, config);
      localStorage.setItem("user_name", user_name);
      navigate("/login");
    } catch (error) {
      console.error("회원 정보 수정 실패:", error);
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setUser({ ...user, user_profile: file });
  };

  const navichat = () => {
    navigate("/chat");
  };

  const navihome = () => {
    navigate("/chat/home");
  };

  const naviset = () => {
    navigate("/chat/set");
  };

  return (
    <div className="chat">
      <div className="chat_menubar">
        <div className="blank0"></div>
        <div className="chat_menubar_button_f" onClick={navihome}></div>
        <div className="chat_menubar_button_c" onClick={navichat}></div>
        <div className="chat_menubar_button_d_c"></div>
        <div className="chat_menubar_button_s" onClick={naviset}></div>
      </div>

      <div className="chat_body">
        <div>
          <div className="chat_header"></div>

          <form onSubmit={onSubmit} className="form-container">
            <h1>회원정보 수정하기</h1>
            <div className="form-group mb-1">
              <input
                type="email"
                className="form-control"
                name="user_idemail"
                placeholder="이메일"
                value={localStorage.user_idemail}
                readOnly
              />
            </div>
            <div className="form-group mb-1">
              <input
                type="password"
                className="form-control"
                name="user_password"
                placeholder="비밀번호"
                value={user_password}
                onChange={handleValueChange}
              />
            </div>

            <div className="form-group mb-1">
              <input
                type="password"
                className="form-control"
                name="user_password2"
                placeholder="비밀번호 확인"
                onChange={passChang}
              />
              <span>{passwordCheck}</span>
            </div>
            <div className="form-group mb-1">
              <input
                type="text"
                className="form-control"
                name="user_name"
                placeholder="이름"
                value={user_name}
                onChange={handleValueChange}
              />
            </div>

            <div className="form-group mb-1">
              <input
                type="text"
                className="form-control"
                name="user_phonenumber"
                placeholder="연락처"
                value={user_phonenumber}
                onChange={handleValueChange}
              />
            </div>
            <div className="form-group mb-1">
              <input
                type="text"
                className="form-control"
                name="user_nickname"
                placeholder="userNickname"
                value={user_nickname}
                onChange={handleValueChange}
              />
            </div>

            <div className="form-group mb-1">
              <label htmlFor="profileImage">프로필 사진</label>
              <input
                type="file"
                className="form-control"
                id="profileImage"
                accept="image/*"
                onChange={handleFileChange}
              />
            </div>

            <button type="submit" className="btn btn-primary">
              회원정보 수정하기
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditInfo;
