import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { productActions } from "../../toolkit/actions/product_action";
import "./Search.css";
import TableRow from "./TableRow";
import Productlist from "../product/Productlist";

const Search = () => {
  //기본 함수 선언
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const product_code = 37; // 여기서 product_code 받음

  // 물품관련
  const productDetail = useSelector((state) => state.product.productDetail);
  const productImgDetail = useSelector(
    (state) => state.product.productImgDetail
  );
  const imagePath = `/shopimg/${productImgDetail.product_img0}`;
  //이동
  const handleimgClick1 = () => {
    navigate(`/search/user/product/${product_code}`);
    console.log("---------", product_code);
  };

  const product_name = "파워에이드";
  // const productList = useSelector((state) => state.product.productList);

  // const productImages = useSelector((state) => state.product.productImage);
  const { productList, productImages } = useSelector((state) => state.product);
  const search = () => {
    dispatch(productActions.getProductList(product_name));
    // console.log(productList[0].product_price);
  };

  //반응
  useEffect(() => {
    console.log("+++++++++", product_code);

    // 특정 파라미터 값(예: product_code)을 콘솔에 출력합니다.

    dispatch(productActions.getProductDetail(product_code));

    dispatch(productActions.getProductimgDownload(product_code));
  }, []);

  return (
    <div>
      <button onClick={search}>파워에이드</button>

      <div>
        {/* <div>
          <img
            src={imagePath}
            alt="product_img0"
            width="300"
            height="300"
            onClick={handleimgClick1}
            className="cursor-pointer"
          />
          <div>
            <div>{productDetail.product_name}</div>
            <div>{productDetail.product_price}</div>
          </div>
        </div> */}
        {/* <div>
          {productList &&
            productList.map((product) => {
              return <TableRow product={product} key={product.product_code} />;
            })}
        </div> */}
        {/* <div>
          
          {productList &&
            productList.map((product) => (
              <div key={product.product_code}>
                <h2>{product.product_name}</h2>{" "}
                
                <p>Price: {product.product_price}</p>
              </div>
            ))}
        </div> */}
        <div>
          <img
            src="./public/shopimg/39_product_img0_제목 없음.png"
            alt="파워에이드"
          />
        </div>
        <div>
          {productList &&
            productList.map((product) => (
              <div key={product.product_code}>
                {productImages[product.product_code].product_img0}
                <h2>{product.product_name}</h2>
                <p>Price: ${product.product_price}</p>
                <p>Code: {product.product_code}</p>
                {productImages[product.product_code]?.product_img0 && (
                  <img
                    src={`/shopimg/${
                      productImages[product.product_code].product_img0
                    }`}
                    alt={`${product.product_name}`}
                    width="300"
                    height="300"
                  />
                )}
                {/* 추가적으로 product_img1, product_img2에 대해서도 같은 방식으로 이미지를 처리할 수 있습니다. */}
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default Search;
