import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
// API URL 설정파일 불러오기
import API_URL from "./config";

const App = () => {
  // 주문 정보와 결제 결과를 관리하는 상태
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
    paymentMethodId: "",
  });
  const [product, setProducts] = useState([]);
  const [paymentResult, setPaymentResult] = useState("");

  // 상품 정보를 불러오는 효과
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(`${API_URL}/product/content/3`);
        setProducts(
          response.data.map((product) => ({ ...product, quantity: 1 }))
        ); // 모든 상품에 대해 초기 수량을 1로 설정
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };

    fetchProducts();
  }, []);

  console.log("product.product_name", product.product_name);

  // 주문 정보 변경 처리 함수
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo({ ...orderInfo, [name]: value });
  };

  // 상품 수량 변경 처리 함수
  const handleQuantityChange = (index, quantity) => {
    const updatedProducts = [...product];
    updatedProducts[index].quantity = quantity;
    setProducts(updatedProducts);
  };

  // 주문 제출 처리 함수
  const submitOrder = async () => {
    try {
      const orderData = {
        ...orderInfo,
        products: product.map((product) => ({
          productId: product.id,
          quantity: product.quantity,
        })),
      };
      const response = await axios.post(`${API_URL}/orders`, orderData);
      if (response.status === 200) {
        setPaymentResult("Order has been successfully processed.");
      } else {
        setPaymentResult("There was a problem processing your order.");
      }
    } catch (error) {
      console.error("Order submission error:", error);
      setPaymentResult("Error processing order.");
    }
  };

  return (
    <div className="app">
      {/* 주문 정보 입력 폼 */}
      <div>
        <h2>배송지</h2>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="Delivery Address"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="recipient"
          placeholder="Recipient"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="Delivery Instructions"
          onChange={handleInputChange}
        />
        {/* 상품 목록 */}
        <h3>상품 목록</h3>
        {product.map((product, index) => (
          <div key={product.id}>
            <span>
              {product.product_name} - ${product.product_price}
            </span>
            <input
              type="number"
              value={product.product_count}
              onChange={(e) =>
                handleQuantityChange(index, parseInt(e.target.value, 10))
              }
              min="1"
            />
          </div>
        ))}
        <button onClick={submitOrder}>결제하기</button>
      </div>
      {paymentResult && <div>{paymentResult}</div>}
    </div>
  );
};

export default App;
