import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./Chat.css";

function Chat() {
  const [webSocket, setWebSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isConnected, setIsConnected] = useState(false);
  const location = useLocation();
  let user_id = location.state?.user_id;
  let chatRoomId = location.state?.chatRoomId;
  // WebSocket 연결 설정

  //연결
  const connectWebSocket = () => {
    console.log(user_id);

    const ws = new WebSocket("ws://localhost:8090/ws/chat");

    ws.onopen = () => {
      console.log("WebSocket 서버에 연결됨");
      setIsConnected(true);
      const enterMessage = {
        messageType: "ENTER",
        chatRoomId: chatRoomId,
        senderId: user_id,
        message: "",
      };
      ws.send(JSON.stringify(enterMessage));
      console.log("입장", enterMessage);
    };

    ws.onmessage = (event) => {
      const message = JSON.parse(event.data);
      setMessages((prevMessages) => [...prevMessages, message]);
    };

    ws.onclose = (e) => {
      console.log(e.code);
      console.log("WebSocket 서버에서 연결 끊김");
      setIsConnected(false);
    };

    setWebSocket(ws);
  };

  useEffect(() => {
    connectWebSocket();
    // 컴포넌트 언마운트 시 WebSocket 연결 해제
    return () => {
      if (webSocket) {
        webSocket.close();
      }
    };
  }, [user_id]);

  const sendMessage = () => {
    if (webSocket) {
      const message = {
        messageType: "TALK",
        chatRoomId: chatRoomId, // 채팅방에 따라 동적으로 설정해야 함
        senderId: user_id, // 사용자의 ID여야 함
        message: inputMessage,
      };
      console.log(message);
      webSocket.send(JSON.stringify(message));
      setInputMessage("");
    }
  };

  const changeFriendName = (event) => {
    user_id = event;
    retryConnection();
  };

  const retryConnection = () => {
    if (!isConnected) {
      connectWebSocket();
    }
  };

  return (
    <div>
      {isConnected ? (
        <>
          <h2>채팅방</h2>
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
          />
          <button onClick={sendMessage}>보내기</button>

          <div className="chat_content_box">
            {messages.map(
              (msg, index) =>
                msg.message && (
                  <p key={index}>
                    {msg.senderId}: {msg.message}
                  </p>
                )
            )}
          </div>
        </>
      ) : (
        <div>
          <div>친구목록</div>
          <div
            className="friend_button"
            onClick={() => {
              changeFriendName("규연");
              retryConnection();
            }}
          >
            규연
          </div>
          <div
            className="friend_button"
            onClick={() => {
              changeFriendName("민호");
              retryConnection();
            }}
          >
            민호
          </div>
          <div
            className="friend_button"
            onClick={() => {
              changeFriendName("도일");
              retryConnection();
            }}
          >
            도일
          </div>
          <div
            className="friend_button"
            onClick={() => {
              changeFriendName("규철");
              retryConnection();
            }}
          >
            규철
          </div>
          <div></div>
        </div>
      )}
    </div>
  );
}

export default Chat;
