import React, { useEffect } from "react";
import "./Productlist.css";
import { useNavigate, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { productActions } from "../../toolkit/actions/product_action";

const Productlist = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  //   const { product_code } = useParams();

  const product_code = 36;

  const savemenunavi = (e) => {
    navigate("/seller/product/save");
  };

  const listmenunavi = (e) => {
    navigate("/seller/product/list");
  };

  const productDetail = useSelector((state) => state.product.productDetail);

  const productImgDetail = useSelector(
    (state) => state.product.productImgDetail
  );
  useEffect(() => {
    console.log("+++++++++", product_code);

    // 특정 파라미터 값(예: product_code)을 콘솔에 출력합니다.

    dispatch(productActions.getProductDetail(product_code));
    dispatch(productActions.getProductimgDownload(product_code));
  }, []);
  const imagePath = `/shopimg/${productImgDetail.product_img0}`;
  return (
    <>
      <h1 className="head">물품 리스트</h1>
      <div className="seller_body">
        <div className="seller_menu_box">
          <table>
            <tbody>
              <button className="seller_menu_box_list" onClick={listmenunavi}>
                물품 리스트
              </button>
              <button className="seller_menu_box_list" onClick={savemenunavi}>
                물품 등록
              </button>
              <button className="seller_menu_box_list">정산</button>
            </tbody>
          </table>
        </div>
        <form>
          <table>
            <tbody className="seller_list_body">
              <tr className="seller_list_body_list">
                <th>seller_id</th>
                <td>{productDetail.product_name}</td>
              </tr>
              <tr className="seller_list_body_list">
                <th></th>
                <td>{productDetail.product_price}</td>
              </tr>
              <tr className="seller_list_body_list">
                <th></th>
                <td>{productDetail.product_count}</td>
              </tr>
              <tr className="seller_list_body_list">
                <th></th>
                <td>
                  <img
                    src={imagePath}
                    alt="product_img0"
                    width="300"
                    height="300"
                  />
                </td>
              </tr>
            </tbody>
          </table>
        </form>
      </div>
    </>
  );
};

export default Productlist;
