import { useNavigate } from "react-router-dom";
import "./Productsave.css";
import { useState } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";

const Porductsave = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [product, setProduct] = useState({
    seller_id: "",
    category_code: "",
    product_name: "",
    product_price: "",
    product_count: "",
  });

  const handleValueChange = (e) => {
    setProduct((prev) => {
      return { ...prev, [e.target.name]: e.target.value };
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await axios
      .post(`/product/save`, product)
      .then((response) => {
        navigate("/");
        alert("성공적으로 등록되었습니다");
      })
      .catch((error) => {
        console.log(error);
        alert("등록에 실패하였습니다");
      });
  };

  //다운로드
  const handleDownload = async () => {
    const boardFile = await dispatch(
      boardActions.getBoardDownload(boardDetail.upload, config)
    );

    // 여기서 부터 붙여넣기한 부분
    const fileName = boardDetail.upload.substring(
      boardDetail.upload.indexOf("_") + 1
    );
    console.log(fileName);

    const url = window.URL.createObjectURL(new Blob([boardFile]), {
      type: "application/octet-stream",
    });

    console.log("url:", url);
    const link = document.createElement("a"); //<a></a>
    link.href = url;
    link.setAttribute("download", fileName);
    link.style.cssText = "display:none";
    document.body.appendChild(link);
    link.click();
    link.remove();
  };

  return (
    <>
      <h1 className="head">물품등록</h1>
      <form className="save" onSubmit={onSubmit}>
        <table>
          <tbody>
            <tr>
              <th>seller_id</th>
              <td>
                <input
                  type="text"
                  name="seller_id"
                  onChange={handleValueChange}
                ></input>
              </td>
            </tr>
            <tr>
              <th>category_code</th>
              <td>
                <input
                  type="text"
                  name="category_code"
                  onChange={handleValueChange}
                ></input>
              </td>
            </tr>
            <tr>
              <th>product_name</th>
              <td>
                <input
                  type="text"
                  name="product_name"
                  onChange={handleValueChange}
                ></input>
              </td>
            </tr>
            <tr>
              <th>product_price</th>
              <td>
                <input
                  type="text"
                  name="product_price"
                  onChange={handleValueChange}
                ></input>
              </td>
            </tr>
            <tr>
              <th>product_count</th>
              <td>
                <input
                  type="text"
                  name="product_count"
                  onChange={handleValueChange}
                ></input>
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit" className="btn btn-primary">
          등록완료
        </button>
      </form>
    </>
  );
};

export default Porductsave;
