import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { chatActions } from "../../toolkit/actions/chat_Action";
import "./ChatHome.css";

const ChatHome = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [chatFriend, setChatFriend] = useState({
    chatroom_id: "",
    user_id1: 1,
    user_id2: "",
    chatroom_name: "",
  });

  const { friendList = [] } = useSelector((state) => state.chat);

  const handleValueChange = (e) => {
    setChatFriend((prev) => {
      return { ...prev, [e.target.name]: e.target.value };
    });
  };

  const navichatlist = () => {
    navigate("/ws/chat", { state: { user_id: "1", chatRoomId: "2" } });
  };
  const home = () => {
    navigate("/");
  };

  const onSubmit = async () => {
    if (!chatFriend.user_id2.trim()) {
      alert("친구 ID를 입력해주세요."); // 빈값일 때 경고 메시지 표시
      return; // 함수 실행 종료
    }

    console.log(chatActions.getAddChatFriend(chatFriend));
    const result = await dispatch(chatActions.getAddChatFriend(chatFriend));
    console.log(result);
    alert(`${result}`);
  };

  useEffect(() => {
    console.log("로그인한 유저", chatFriend.user_id1);
    dispatch(chatActions.getFriendList(chatFriend.user_id1));
    console.log("친구리스트 받기", friendList);
  }, []);

  return (
    <>
      <div className="chat_home">
        <div className="temp_shop" onClick={home}>
          친구
        </div>
        <div className="chat_home_header">
          <div>
            <input type="text" name="user_id2" onChange={handleValueChange} />
          </div>
          <button type="submit" onClick={onSubmit}>
            친구추가
          </button>
        </div>
        {/* 친구목록 */}
        <div className="chat_home_body">
          <div className="chat_home_menubar">
            <button className="chm_button">친구</button>
            <button className="chm_button" onClick={navichatlist}>
              채팅
            </button>
          </div>
          <div>
            <div className="chat_user">
              {friendList
                .filter((chat) => chat.user_id1 === chat.user_id2) // 조건에 맞는 항목만 필터링
                .map((chat) => (
                  <div key={chat.chatroom_id} className="chatFriendListbox">
                    <div>
                      <div>나</div>
                    </div>
                  </div>
                ))}
            </div>
            <div>
              {friendList &&
                friendList
                  .filter((chat) => chat.user_id1 !== chat.user_id2)
                  .map((chat) => (
                    <div key={chat.chatroom_id} className="chatFriendListbox">
                      <div className="profile_img">profile_img</div>
                      <div>user_id: {chat.user_id2}</div>
                    </div>
                  ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ChatHome;
