import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const API_URL = "http://localhost:8090";

const App = () => {
  const [product, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState({});
  const [quantity, setQuantity] = useState(1); // 기본 수량을 1로 설정
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    deliveryInstruction: "",
  });
  const [paymentResult, setPaymentResult] = useState("");

  // 상품 목록을 불러오는 효과
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(`${API_URL}/product/content/3`);
        console.log(product.product_id);
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };
    fetchProducts();
  }, []);

  const handleSelectProduct = (product) => {
    setSelectedProduct(product);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value,
    }));
  };

  const handleQuantityChange = (e) => {
    setQuantity(parseInt(e.target.value, 10) || 1);
  };

  const submitOrder = async () => {
    try {
      const response = await axios.post(`${API_URL}/orders`, {
        user_id: 1, // 예시로 1을 사용, 실제 사용자 인증 구현에 따라 변경 필요
        order_date: new Date().toISOString(),
        order_address: orderInfo.delivery_address,
        order_amount: selectedProduct.product_price * quantity,
        product: [
          {
            product_id: selectedProduct.product_id,
            quantity: quantity,
          },
        ],
      });
      if (response.status === 201) {
        setPaymentResult("주문이 성공적으로 처리되었습니다.");
      }
    } catch (error) {
      console.error("주문 처리 중 오류가 발생했습니다.", error);
      setPaymentResult("주문 처리 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="app">
      <h1>상품 목록</h1>
      <ul>
        {product.map((product) => (
          <li
            key={product.productId}
            onClick={() => handleSelectProduct(product)}
          >
            {product.product_name} - ${product.product_price}
          </li>
        ))}
      </ul>
      {selectedProduct.productId && (
        <>
          <h2>선택한 상품: {selectedProduct.product_name}</h2>
          <p>가격: ${selectedProduct.product_price}</p>
          <label>
            수량:
            <input
              type="number"
              value={quantity}
              onChange={handleQuantityChange}
            />
          </label>
          <div>
            <input
              type="text"
              name="deliveryAddress"
              placeholder="배송지 주소"
              onChange={handleChange}
            />
            <input
              type="text"
              name="deliveryInstruction"
              placeholder="배송 요청 사항"
              onChange={handleChange}
            />
            <button onClick={submitOrder}>주문하기</button>
          </div>
        </>
      )}
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default App;
