import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./BlogUserProduct.css";
import { useDispatch, useSelector } from "react-redux";
import { productActions } from "../../toolkit/actions/product_action";

const BlogUserProduct = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { product_code } = useParams();
  console.log("+++++++++", product_code);
  const productDetail = useSelector((state) => state.product.productDetail);

  const onhandlebuybutton = () => {
    // navigate('./buy')
  };

  //   dispatch(productActions.getProductDetail(product_code.product_code));

  useEffect(() => {
    console.log("+++++++++", product_code);

    // 특정 파라미터 값(예: product_code)을 콘솔에 출력합니다.

    dispatch(productActions.getProductDetail(product_code));
  }, []);

  return (
    <>
      <h1 className="headline">blog detail</h1>
      <table className="body">
        <tbody>
          <tr>
            <th>product_code</th>
            <td>{productDetail.product_code}</td>
            <th>product_name</th>
            <td>{productDetail.product_name}</td>
          </tr>
          <tr>
            <th>product_name</th>
            <td>{productDetail.product_name}</td>
          </tr>
          <tr>
            <th>product_price</th>
            <td>{productDetail.product_price}</td>
          </tr>
          <tr>
            <th>product_count</th>
            <td></td>
          </tr>
        </tbody>
      </table>
      <div></div>
      <div class="menu-bar">
        {/* <!-- 메뉴바 내용 --> */}
        <div class="dropdown-menu">
          {/* <!-- 드롭다운 메뉴 --> */}
          옵션 선택
        </div>
        <button class="buy" onClick={onhandlebuybutton}>
          구매하기
        </button>
        <button class="cart" onClick={onhandlebuybutton}>
          장바구니
        </button>
        {/* <!-- 소셜 아이콘 --> */}
        <div className="menu-bar-icorn">
          <img
            src="/images/Instagram_logo.png"
            alt="Instagram"
            className="social-icon"
            width="100"
            height="100"
          />
          <img
            src="/images/blog.png"
            alt="Blog"
            class="social-icon"
            width="100"
            height="100"
          />
        </div>
      </div>
    </>
  );
};

export default BlogUserProduct;
