import axios from "axios";
import { productReducers } from "../createSlice/product_createSlice";
import { useNavigate } from "react-router-dom";
//안씀
function getProductWrite(formData) {
  return async () => {
    // await axios
    //   .post(`/product/save`, formData)
    //   .then((response) => response.data);

    await axios
      .post(`/api/product/save`, formData)
      .then((response) => {
        // 서버로부터의 응답 데이터를 처리합니다.
        const data = response.data;

        // 응답 데이터를 콘솔에 로그로 기록합니다. (필요에 따라)
        console.log("Product_action fromdata", data);

        // 사용자에게 성공 알림을 보냅니다.
        alert("등록성공");

        // 이후의 처리를 위해 응답 데이터를 반환할 수 있습니다.
        return data;
      })

      .catch((error) => {
        console.log(formData);
        console.log(error);
        alert("등록에 실패하였습니다");
      });
  };

  // await axios
  // .post(`/product/save`, product)
  // .then((response) => {
  //   navigate("/");
  //   alert("성공적으로 등록되었습니다");
  // })
  // .catch((error) => {
  //   console.log(error);
  //   alert("등록에 실패하였습니다");
  // });
}

function getProductDetail(product_code) {
  return async (dispatch) => {
    const data = await axios
      .get(`/api/product/content/${product_code}`)
      .then((response) => response.data);

    dispatch(productReducers.getProduct({ data }));
  };
}

function getProductimgDownload(upload) {
  return async (dispatch) => {
    const data = await axios
      .get(`/product/contentdownload/${upload}`)
      .then((response) => response.data);

    return data;
  };
}

export const productActions = {
  getProductWrite,
  getProductDetail,
  getProductimgDownload,
};
