import axios from "axios";
import { productReducers } from "../createSlice/product_createSlice";
//안씀
function getProductWrite(formData) {
  return async () => {
    await axios
      .post(`/product/save`, formData)
      .then((response) => response.data);
  };
}

function getProductDetail(product_code) {
  return async (dispatch) => {
    const data = await axios
      .get(`/product/content/${product_code}`)
      .then((response) => response.data);

    dispatch(productReducers.getProduct({ data }));
  };
}

function getProductimgDownload(upload) {
  return async (dispatch) => {
    const data = await axios
      .get(`/product/contentdownload/${upload}`)
      .then((response) => response.data);

    return data;
  };
}

export const productActions = {
  getProductWrite,
  getProductDetail,
};
