import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createPayment } from "../actions/paymentActions";
import { createOrder } from "../actions/orderActions";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const PaymentPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { productId } = useParams();
  const loggedInUserId = useSelector((state) => state.auth.userId);
  const [orderInfo, setOrderInfo] = useState({
    deliveryInstruction: "",
    quantity: 1,
  });
  const [product, setProduct] = useState(null);
  const [paymentMethods, setPaymentMethods] = useState([]);
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState(null);
  const [paymentResult, setPaymentResult] = useState("");

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`/api/product/${productId}`);
        setProduct(response.data);
      } catch (error) {
        console.error("상품 정보 가져오기 실패:", error);
      }
    };

    const fetchPaymentMethods = async () => {
      try {
        const response = await axios.get("/api/paymentMethods");
        setPaymentMethods(response.data);
      } catch (error) {
        console.error("결제 수단 가져오기 실패:", error);
      }
    };

    fetchProduct();
    fetchPaymentMethods();
  }, [productId]);

  const handleInputChange = (e) => {
    setOrderInfo({ ...orderInfo, [e.target.name]: e.target.value });
  };

  const handleQuantityChange = (e) => {
    setOrderInfo({ ...orderInfo, quantity: parseInt(e.target.value) });
  };

  const handlePaymentMethodChange = (e) => {
    const selectedMethodId = parseInt(e.target.value);
    const selectedMethod = paymentMethods.find(
      (method) => method.methodId === selectedMethodId
    );
    setSelectedPaymentMethod(selectedMethod);
  };

  const submitOrder = () => {
    const loggedInUserId = localStorage.getItem("user_id");

    if (!loggedInUserId) {
      // user_id가 없는 경우 에러 처리
      setPaymentResult("로그인이 필요합니다.");
      return;
    }

    const orderData = {
      userId: loggedInUserId, // user_id를 문자열로 그대로 사용
      deliveryInstruction: orderInfo.deliveryInstruction,
      quantity: orderInfo.quantity,
      totalAmount: product.product_price * orderInfo.quantity,
      orderDate: new Date(),
      orderAmount: product.product_price * orderInfo.quantity,
    };

    const paymentData = {
      methodId: selectedPaymentMethod.methodId,
      transactionDate: new Date(),
      isSuccessful: true,
      orderId: null,
      amount: product.product_price * orderInfo.quantity,
    };

    dispatch(createOrder(orderData))
      .then((response) => {
        const createdOrder = response.data;
        paymentData.orderId = createdOrder.orderId;

        dispatch(createPayment(paymentData))
          .then(() => {
            setPaymentResult("결제가 성공적으로 완료되었습니다.");

            // 정산 정보 생성
            const payoutData = {
              orderId: createdOrder.orderId,
              amount: product.product_price * orderInfo.quantity,
              payoutDate: new Date(),
            };

            axios
              .post("/api/payouts", payoutData)
              .then(() => {
                navigate("/order/complete");
              })
              .catch((error) => {
                console.error("정산 정보 생성 중 오류 발생:", error);
              });
          })
          .catch((error) => {
            setPaymentResult(
              `결제 중 오류가 발생했습니다: ${error.response.data}`
            );
          });
      })
      .catch((error) => {
        setPaymentResult(
          `주문 생성 중 오류가 발생했습니다: ${error.response.data}`
        );
      });
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="payment-page">
      <h2>주문 및 결제</h2>
      <div>
        <label>
          배송 요청사항:
          <input
            type="text"
            name="deliveryInstruction"
            value={orderInfo.deliveryInstruction}
            onChange={handleInputChange}
          />
        </label>
      </div>
      <div>
        <label>
          수량:
          <input
            type="number"
            name="quantity"
            value={orderInfo.quantity}
            onChange={handleQuantityChange}
          />
        </label>
      </div>
      <div>
        <h3>상품 정보</h3>
        <p>상품명: {product.name}</p>
        <p>가격: {product.price}원</p>
        <p>총 결제 금액: {product.price * orderInfo.quantity}원</p>
      </div>
      <div>
        <h3>결제 수단 선택</h3>
        {paymentMethods.map((method) => (
          <label key={method.methodId}>
            <input
              type="radio"
              name="paymentMethod"
              value={method.methodId}
              checked={selectedPaymentMethod?.methodId === method.methodId}
              onChange={handlePaymentMethodChange}
            />
            {method.methodName}
          </label>
        ))}
      </div>
      <button onClick={submitOrder} disabled={!selectedPaymentMethod}>
        주문하기
      </button>
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default PaymentPage;
