import { createSlice } from "@reduxjs/toolkit";

//안씀
//action타입, actioncreater,reducer를 여기에서 설정

let initialState = {
  product: {},
  productDetail: {},
  productImgDetail: {},
};

const productSlice = createSlice({
  name: "product",
  initialState,
  reducers: {
    getProduct(state, action) {
      state.productDetail = action.payload.data;
    },

    getPoductImg(state, action) {
      state.productImgDetail = action.payload.data;
    },
  },
});

const orderSlice = createSlice({
  name: "order",
  initialState: {
    order: null,
    error: null,
  },
  reducers: {
    createOrderSuccess(state, action) {
      state.order = action.payload;
    },
    createOrderFailure(state, action) {
      state.error = action.payload;
    },
  },
});

export const productReducers = productSlice.actions;
export const { createOrderSuccess, createOrderFailure } = orderSlice.actions;
export default productSlice;
