import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

// 설정 파일로부터 API URL 불러오기
const API_URL = "http://localhost:8090";

const App = () => {
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
    quantity: 1,
  });
  const [product, setProduct] = useState(null);
  const [paymentResult, setPaymentResult] = useState("");

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`${API_URL}/product/content/3`);
        setProduct(response.data);
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };
    fetchProduct();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo((prev) => ({ ...prev, [name]: value }));
  };

  const handleQuantityChange = (e) => {
    setOrderInfo((prev) => ({
      ...prev,
      quantity: parseInt(e.target.value, 10) || 1,
    }));
  };

  const submitOrder = async () => {
    try {
      const orderAmount = product.product_price * orderInfo.quantity;
      const response = await axios.post(`${API_URL}/orders`, {
        ...orderInfo,
        products: [
          { productId: product.product_code, quantity: orderInfo.quantity },
        ],
        orderAmount,
      });
      if (response.status === 200) {
        setPaymentResult("주문이 성공적으로 처리되었습니다.");
      } else {
        setPaymentResult("주문 처리에 실패했습니다.");
      }
    } catch (error) {
      console.error("주문 처리 중 오류가 발생했습니다.", error);
      setPaymentResult("주문 처리 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="app">
      <div className="order-form">
        <h2>주문 및 결제</h2>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="배송지"
          value={orderInfo.deliveryAddress}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="recipient"
          placeholder="받는 사람"
          value={orderInfo.recipient}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="배송 요청사항"
          value={orderInfo.deliveryInstruction}
          onChange={handleInputChange}
        />

        {product && (
          <div className="product-info">
            <h3>상품 정보</h3>
            <p>제품명: {product.product_name}</p>
            <p>가격: ${product.product_price}</p>
            <label>
              수량 선택:
              <input
                type="number"
                name="quantity"
                value={orderInfo.quantity}
                onChange={handleQuantityChange}
              />
            </label>
            <p>수량: {orderInfo.quantity}</p>
            <p>총 금액: ${product.product_price * orderInfo.quantity}</p>
          </div>
        )}
        <button onClick={submitOrder}>주문하기</button>
      </div>
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default App;
