import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const API_URL = "http://localhost:8090";

const App = () => {
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    deliveryInstruction: "",
    quantity: 1,
  });
  const [product, setProduct] = useState({});
  const [paymentResult, setPaymentResult] = useState("");

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/product/content/3`);
        setProduct(response.data);
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };
    fetchProduct();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo((prev) => ({ ...prev, [name]: value }));
  };

  const handleQuantityChange = (e) => {
    setOrderInfo((prev) => ({
      ...prev,
      quantity: parseInt(e.target.value, 10) || 1,
    }));
  };

  const submitOrder = async () => {
    try {
      const orderAmount = (product.product_price || 0) * orderInfo.quantity;
      const response = await axios.post(`${API_URL}/api/orders`, {
        ...orderInfo,
        products: [
          {
            productId: product.product_code,
            quantity: orderInfo.quantity,
          },
        ],
        orderAmount,
      });
      if (response.status === 201) {
        setPaymentResult("주문이 성공적으로 처리되었습니다.");
        // 주문 완료 후 추가 동작을 수행할 수 있습니다.
      } else {
        setPaymentResult("주문 처리에 실패했습니다.");
      }
    } catch (error) {
      console.error("주문 처리 중 오류가 발생했습니다.", error);
      setPaymentResult("주문 처리 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="app">
      <div className="order-form">
        <h2>주문 및 결제</h2>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="배송지"
          value={orderInfo.deliveryAddress}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="배송 요청사항"
          value={orderInfo.deliveryInstruction}
          onChange={handleInputChange}
        />
        {product.product_name && (
          <div className="product-info">
            <h3>상품 정보</h3>
            <p>제품명: {product.product_name}</p>
            <p>가격: {product.product_price}원</p>
            <label>
              수량 선택:
              <input
                type="number"
                name="quantity"
                value={orderInfo.quantity}
                onChange={handleQuantityChange}
              />
            </label>
            <p>총 금액: {product.product_price * orderInfo.quantity}원</p>
          </div>
        )}
        <button onClick={submitOrder}>주문하기</button>
      </div>
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default App;
