import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
    paymentMethodId: "",
  });
  const [products, setProducts] = useState([]);
  const [paymentResult, setPaymentResult] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/products`
        );
        setProducts(response.data);
      } catch (error) {
        console.error("상품 데이터를 불러오는 중 오류가 발생했습니다:", error);
      }
    };
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo({ ...orderInfo, [name]: value });
  };

  const submitOrder = async () => {
    const orderAmount = products.reduce(
      (total, product) => total + product.productPrice * product.quantity,
      0
    );
    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/orders`,
        {
          ...orderInfo,
          products: products.map((product) => ({
            productId: product.productId,
            quantity: product.quantity,
          })),
          orderAmount,
        }
      );
      if (response.status === 200) {
        setPaymentResult("주문이 성공적으로 처리되었습니다.");
      }
    } catch (error) {
      console.error("주문 처리 중 오류가 발생했습니다.", error);
      setPaymentResult("주문 처리 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="app">
      {/* 렌더링 및 입력 폼 코드 */}
      <div className="payment-button">
        <button onClick={submitOrder}>주문하기</button>
      </div>
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default App;
