import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
const App = () => {
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
    paymentMethodId: "",
  });

  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // 백엔드 API로부터 상품 데이터를 불러옵니다.
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/products`
        );
        setProducts(response.data); // 상태를 업데이트하여 화면에 표시합니다.
      } catch (error) {
        console.error("상품 데이터를 불러오는 중 오류가 발생했습니다:", error);
      }
    };

    fetchProducts();
  }, []); // 빈 배열을 전달하여 컴포넌트가 마운트될 때 단 한 번만 실행됩니다.

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo({ ...orderInfo, [name]: value });
  };

  const submitOrder = async () => {
    const orderAmount = products.productPrice * products.quantity; // 결제 금액 계산
    try {
      // 주문 정보 및 결제 금액을 포함하여 백엔드로 전송
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/orders`,
        {
          ...orderInfo,
          productId: products.productId,
          quantity: products.quantity,
          orderAmount,
        }
      );
      if (response.status === 200) {
        alert("주문이 성공적으로 처리되었습니다.");
      }
    } catch (error) {
      console.error("주문 처리 중 오류가 발생했습니다.", error);
      alert("주문 처리 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="app">
      <div className="order-form">
        <h2>주문 및 결제</h2>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="배송지"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="recipient"
          placeholder="받는 사람"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="배송 요청사항"
          onChange={handleInputChange}
        />
        <select name="paymentMethodId" onChange={handleInputChange}>
          <option value="">결제 수단 선택</option>
          <option value="1">신용카드</option>
          <option value="2">계좌이체</option>
          <option value="3">기타</option>
        </select>
        <h3>상품 목록</h3>
        <div className="product-list">
          {products.map((product) => (
            <div key={product.productId} className="product">
              <h3>{product.productName}</h3>
              <p>가격: ${product.productPrice}</p>
              <p>수량: {product.quantity}</p>
              <p>색상: {product.productColor}</p>
              <p>사이즈: {product.productSize}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
