import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

// 설정 파일로부터 API URL 불러오기 (예: http://localhost:8090)
import API_URL from "./config";

const App = () => {
  const [product, setProduct] = useState(null);
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
    quantity: 1, // 기본 수량을 1로 설정
  });
  const [paymentResult, setPaymentResult] = useState("");

  // 단일 상품 정보를 불러오는 효과
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`${API_URL}/product/content/3`);
        setProduct(response.data); // 단일 상품 정보를 상태에 저장
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };
    fetchProduct();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo({ ...orderInfo, [name]: value });
  };

  const handleQuantityChange = (e) => {
    setOrderInfo({ ...orderInfo, quantity: parseInt(e.target.value, 10) });
  };

  const submitOrder = async () => {
    // 주문 정보를 백엔드로 전송하는 로직 구현
  };

  return (
    <div className="app">
      <div className="order-form">
        <h2>주문 및 결제</h2>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="배송지"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="recipient"
          placeholder="받는 사람"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="배송 요청사항"
          onChange={handleInputChange}
        />
        <select name="paymentMethodId" onChange={handleInputChange}>
          <option value="">결제 수단 선택</option>
          <option value="1">신용카드</option>
          <option value="2">계좌이체</option>
          <option value="3">기타</option>
        </select>
      </div>
      {product && (
        <>
          <div>
            <h2>Order and Payment</h2>
            <p>제품명: {product.product_name}</p>
            <p>Price: ${product.product_price}</p>
            <label>
              Quantity:
              <input
                type="number"
                name="quantity"
                value={orderInfo.quantity}
                onChange={handleQuantityChange}
              />
            </label>
            <button onClick={submitOrder}>Submit Order</button>
          </div>

          {paymentResult && <div>{paymentResult}</div>}
        </>
      )}
    </div>
  );
};

export default App;
