import axios from "axios";

export const createPayment = (paymentData) => {
  return (dispatch) => {
    axios
      .post("/api/transactions/createPayment", paymentData)
      .then((response) => {
        dispatch({ type: "CREATE_PAYMENT_SUCCESS" });
      })
      .catch((error) => {
        dispatch({ type: "CREATE_PAYMENT_FAILURE", error });
      });
  };
};
