import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  // 상태 관리
  const [deliveryInfo, setDeliveryInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
  });
  const [orderDetails, setOrderDetails] = useState({
    productId: 1, // 예시 ID, 실제 상품 ID로 대체 필요
    quantity: 1,
  });

  const [paymentMethod, setPaymentMethod] = useState(""); // 결제 수단 ID
  const [paymentResult, setPaymentResult] = useState(""); // 결제 결과 메시지

  // 사용자 입력 처리 함수
  const handleChange = (e) => {
    const { name, value } = e.target;
    setDeliveryInfo((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // 결제 처리 함수
  const handlePayment = async () => {
    try {
      // 주문 정보 백엔드로 전송
      const orderResponse = await axios.post("/api/orders", {
        ...deliveryInfo,
        ...orderDetails,
      });

      if (orderResponse.status === 201) {
        // 결제 요청
        const paymentResponse = await axios.post("/api/payments", {
          orderId: orderResponse.data.orderId,
          methodId: paymentMethod,
          amount: 26702, // 결제 금액, 실제 계산 로직 필요
        });

        if (paymentResponse.status === 200) {
          setPaymentResult("결제가 성공적으로 처리되었습니다.");
        }
      }
    } catch (error) {
      setPaymentResult("결제 처리 중 오류가 발생했습니다.");
      console.error("Payment error:", error);
    }
  };

  return (
    <div className="app">
      {/* 배송 정보 입력 폼 */}
      <div className="order-payment">
        <h2>Order / Payment</h2>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="배송지"
          onChange={handleChange}
        />
        <input
          type="text"
          name="recipient"
          placeholder="배송지 변경"
          onChange={handleChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="부재시 집 앞에 넣어주세요"
          onChange={handleChange}
        />
      </div>

      {/* 주문 상세 정보 */}
      {/* 여기서는 단순화를 위해 하드코딩, 실제로는 상품 목록에서 선택하도록 구현 필요 */}
      <div className="order-details">{/* 상품 정보, 수량, 가격 등 표시 */}</div>

      {/* 결제 정보 및 버튼 */}
      <div className="payment-button">
        <button onClick={handlePayment}>결제하기</button>
      </div>

      {/* 결제 결과 메시지 */}
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default App;
