import axios from "axios";
import { productReducers } from "../createSlice/product_createSlice";
import { useNavigate } from "react-router-dom";
//안씀
function getProductWrite(formData) {
  return async () => {
    await axios
      .post(`/api/product/save`, formData)
      .then((response) => {
        // 서버로부터의 응답 데이터를 처리합니다.
        const data = response.data;

        console.log("Product_action fromdata", formData);

        alert("등록성공");

        return data;
      })

      .catch((error) => {
        console.log(formData);
        console.log(error);
        alert("등록에 실패하였습니다");
      });
  };

  // await axios
  // .post(`/product/save`, product)
  // .then((response) => {
  //   navigate("/");
  //   alert("성공적으로 등록되었습니다");
  // })
  // .catch((error) => {
  //   console.log(error);
  //   alert("등록에 실패하였습니다");
  // });
}

function getProductDetail(product_code) {
  return async (dispatch) => {
    const data = await axios
      .get(`/api/product/content/${product_code}`)
      .then((response) => response.data);

    dispatch(productReducers.getProduct({ data }));
  };
}

