import React, { useState, useEffect } from "react";
import axios from "axios";
import API_URL from "./config";
import "./App.css";

const App = () => {
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
    paymentMethodId: "",
  });

  // products를 배열에서 객체로 변경
  const [product, setProduct] = useState({});
  console.log(product.productName);
  const [paymentResult, setPaymentResult] = useState("");

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`${API_URL}/product/content/3`);
        setProduct(response.data);
      } catch (error) {
        console.error("상품 데이터를 불러오는 중 오류가 발생했습니다:", error);
      }
    };
    fetchProduct();
  }, []);

  console.log("현재 상품 상태:", product.product_name);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo({ ...orderInfo, [name]: value });
  };

  const submitOrder = async () => {
    try {
      const response = await axios.post(`${API_URL}/orders`, {
        ...orderInfo,
        products: [
          {
            productId: product.productId,
            quantity: 1, // 예시로 1을 사용, 실제 양식에 맞게 수정 필요
          },
        ],
        orderAmount: product.productPrice, // 예시로 productPrice 사용, 실제 계산 로직 필요
      });
      if (response.status === 200) {
        setPaymentResult("주문이 성공적으로 처리되었습니다.");
      }
    } catch (error) {
      console.error("주문 처리 중 오류가 발생했습니다.", error);
      setPaymentResult("주문 처리 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="app">
      {/* 렌더링 및 입력 폼 코드 */}
      <div className="order-form">
        <h2>주문 및 결제</h2>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="배송지"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="recipient"
          placeholder="받는 사람"
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="배송 요청사항"
          onChange={handleInputChange}
        />
        <select name="paymentMethodId" onChange={handleInputChange}>
          <option value="">결제 수단 선택</option>
          <option value="1">신용카드</option>
          <option value="2">계좌이체</option>
          <option value="3">기타</option>
        </select>
        <div className="order-form">
          <h3>상품 정보</h3>
          <div className="product-info">
            {product.productName ? ( // productName이 존재하는 경우에만 상품 정보 렌더링
              <>
                <h2>{product.productName}</h2>
                <p>가격: ${product.productPrice}</p>
                <p>색상: {product.productColor}</p>
                <p>사이즈: {product.productSize}</p>
              </>
            ) : (
              <p>상품 정보를 불러오는 중...</p> // 상품 정보가 아직 로드되지 않았을 경우
            )}
          </div>
          <div className="payment-button">
            <button onClick={submitOrder}>주문하기</button>
          </div>
          {paymentResult && <p>{paymentResult}</p>}
        </div>
      </div>
    </div>
  );
};

export default App;
