import axios from "axios";
import { chatRoomReducers } from "../reducers/chatReducer";
import { useDispatch } from "react-router-dom";

// 친구 추가
function getFriendInsert(user_id2) {
  return async (dispatch) => {
    await axios
      .post(`/api/chat/friendinsert`, user_id2)
      .then((response) => {
        const data = response.data;
        console.log(data);
        alert(data);
        return data;
      })
      .catch((error) => {
        console.log(error);
        alert("친구추가실패");
      });
  };
}

// 친구 목록
function getFriendList(user_id1) {
  return async (dispatch) => {
    const data = await axios
      .get(`/api/chat/friendlist/${user_id1}`)
      .then((Response) => Response.data);
    console.log("Map", data);
    dispatch(chatRoomReducers.getFriendList(data));
  };
}

function getNLPsearch(text) {
  return async (dispatch) => {
    try {
      const response = await axios.post(
        `/api/ai/predict/${encodeURIComponent(text)}`
      );
      const data = response.data;
      console.log("NLP 예측 결과:", data);

      // 예측 결과를 사용하여 모달 창 열기 (예시)
      showModal(data.predictedLabels);
    } catch (error) {
      console.error("NLP 예측 오류:", error);
    }
  };
}

// 모달 창에서 레이블 선택 시 호출되는 함수 (예시)
function handleLabelSelect(label) {
  const keyword = label.label;
  const searchUrl = `https://search.wemakeprice.com/search?keyword=${encodeURIComponent(
    keyword
  )}`;
  window.open(searchUrl, "_blank");
}

// 모달 창 렌더링 함수 (예시)
function showModal(predictedLabels) {
  if (!predictedLabels) {
    // predictedLabels가 undefined인 경우 처리
    return;
  }

  const modalContent = document.createElement("div");
  const labelList = document.createElement("ul");

  predictedLabels.forEach((label) => {
    const listItem = document.createElement("li");
    const labelText = `${label.label} (확률: ${(
      label.probability * 100
    ).toFixed(2)}%)`;
    listItem.textContent = labelText;
    listItem.addEventListener("click", () => handleLabelSelect(label));
    labelList.appendChild(listItem);
  });

  modalContent.appendChild(labelList);
  document.body.appendChild(modalContent);
}

export const chatActions = {
  getFriendInsert,
  getFriendList,
  getNLPsearch,
};
