async function getProductWrite(formData) {
  try {
    const response = await axios.post(`/api/product/save`, formData);
    console.log(response.data); // 성공 응답 데이터를 콘솔에 로그합니다.
    // 추가적인 성공 처리 로직이 여기에 올 수 있습니다.
  } catch (error) {
    console.error(error);
    alert("등록에 실패하였습니다");
    // 추가적인 에러 처리 로직이 여기에 올 수 있습니다.
  }
}
function getProductDetail(product_code) {
  return async (dispatch) => {
    const data = await axios
      .get(`/api/product/content/${product_code}`)
      .then((response) => response.data);

    dispatch(productReducers.getProduct({ data }));
  };
}

