const initialState = {
  order: null,
  error: null,
};

const orderReducer = (state = initialState, action) => {
  switch (action.type) {
    case "CREATE_ORDER_SUCCESS":
      return { ...state, order: action.payload };
    case "CREATE_ORDER_FAILURE":
      return { ...state, error: action.error };
    default:
      return state;
  }
};

export default orderReducer;
