import React, { useState, useEffect } from "react";
import "./Chat.css";
import { useNavigate } from "react-router-dom";

function Chat() {
  const [webSocket, setWebSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [userId, setUserId] = useState("");
  const [recipientId, setRecipientId] = useState("");
  const [view, setView] = useState(0); // 추가: 화면 상태 (0: 친구 목록, 1: 채팅)

  const connect = () => {
    if (!webSocket || webSocket.readyState === WebSocket.CLOSED) {
      const ws = new WebSocket(`ws://localhost:8090/ws/chat?userId=${userId}`);
      ws.onopen = () => console.log("Connected to the chat server");
      ws.onmessage = (event) => {
        const message = JSON.parse(event.data);
        setMessages((prevMessages) => [...prevMessages, message]);
      };
      ws.onerror = (event) => console.error("WebSocket error:", event);
      ws.onclose = (event) =>
        console.log("WebSocket connection closed:", event);
      setWebSocket(ws);
    } else {
      console.log("WebSocket is already connected or connecting.");
    }
  };

  const sendMessage = () => {
    if (
      webSocket &&
      inputMessage !== "" &&
      userId !== "" &&
      recipientId !== ""
    ) {
      const messageData = {
        sender_id: userId,
        message: inputMessage,
        recipient_id: recipientId,
      };
      webSocket.send(JSON.stringify(messageData));
      setMessages((prevMessages) => [...prevMessages, messageData]);
      setInputMessage("");
    }
  };

  const disconnect = () => {
    if (webSocket) {
      webSocket.close();
    }
  };

  return (
    <div className="chat_body">
      <input
        type="text"
        placeholder="Enter your ID"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        disabled={webSocket && webSocket.readyState === WebSocket.OPEN}
      />
      <button onClick={connect} disabled={userId.trim() === ""}>
        Connect
      </button>
      <button onClick={disconnect}>Disconnect</button>

      {view === 0 && (
        <div>
          <button
            onClick={() => {
              setRecipientId("A");
              setView(1);
            }}
          >
            Talk with A
          </button>
          <button
            onClick={() => {
              setRecipientId("B");
              setView(1);
            }}
          >
            Talk with B
          </button>
          <button
            onClick={() => {
              setRecipientId("C");
              setView(1);
            }}
          >
            Talk with C
          </button>
        </div>
      )}

      {view === 1 && (
        <div>
          <div>{recipientId}</div>
          <button
            onClick={() => {
              setView(0);
            }}
          >
            뒤로
          </button>
          <ul>
            {messages
              .filter(
                (msg) =>
                  (msg.sender_id === userId &&
                    msg.recipient_id === recipientId) ||
                  (msg.sender_id === recipientId && msg.recipient_id === userId)
              )
              .map((msg, index) => (
                <li
                  key={index}
                  className={`message ${
                    msg.sender_id === userId ? "my-message" : "other-message"
                  }`}
                >
                  {msg.sender_id === userId ? "" : `${msg.sender_id}: `}
                  {msg.message}
                </li>
              ))}
          </ul>
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={(e) => e.key === "Enter" && sendMessage()}
          />
          <button
            onClick={sendMessage}
            disabled={!inputMessage.trim() || !userId.trim() || !recipientId}
          >
            Send
          </button>
        </div>
      )}
    </div>
  );
}

export default Chat;
