import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createPayment } from "../../toolkit/actions/paymentActions";
import { createOrder } from "../../toolkit/actions/orderActions";
import { useNavigate, useParams } from "react-router-dom";
import { productActions } from "../../toolkit/actions/product_action";
import "./PaymentPage.css";

const PaymentPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { product_code } = useParams();
  console.log("order++", product_code);

  const [orderInfo, setOrderInfo] = useState({
    quantity: 1,
  });

  const productDetail = useSelector((state) => state.product.productDetail);
  const productImgDetail = useSelector(
    (state) => state.product.productImgDetail
  );

  const [paymentMethod, setPaymentMethod] = useState("");
  const [paymentResult, setPaymentResult] = useState("");

  const handleQuantityChange = (e) => {
    setOrderInfo({ ...orderInfo, quantity: parseInt(e.target.value) });
  };

  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const submitOrder = () => {
    const user_id = localStorage.getItem("user_id");

    if (!user_id) {
      setPaymentResult("로그인이 필요합니다.");
      return;
    }

    const orderData = {
      userId: user_id,
      quantity: orderInfo.quantity,
      totalAmount: productDetail.product_price * orderInfo.quantity,
      orderDate: new Date(),
      orderAmount: productDetail.product_price * orderInfo.quantity,
    };

    const paymentData = {
      methodId: parseInt(paymentMethod),
      transactionDate: new Date(),
      isSuccessful: true,
      orderId: null,
      amount: productDetail.product_price * orderInfo.quantity,
    };

    dispatch(createOrder(orderData))
      .then((response) => {
        const createdOrder = response.data;
        paymentData.orderId = createdOrder.orderId;

        dispatch(createPayment(paymentData))
          .then(() => {
            setPaymentResult("결제가 성공적으로 완료되었습니다.");

            const payoutData = {
              orderId: createdOrder.orderId,
              amount: productDetail.product_price * orderInfo.quantity,
              payoutDate: new Date(),
            };

            axios
              .post("/api/payouts", payoutData)
              .then(() => {
                navigate("/order/complete");
              })
              .catch((error) => {
                console.error("정산 정보 생성 중 오류 발생:", error);
              });
          })
          .catch((error) => {
            setPaymentResult(
              `결제 중 오류가 발생했습니다: ${error.response.data}`
            );
          });
      })
      .catch((error) => {
        setPaymentResult(
          `주문 생성 중 오류가 발생했습니다: ${error.response.data}`
        );
      });
  };

  useEffect(() => {
    console.log("+++++++++", product_code);
    dispatch(productActions.getProductDetail(product_code));
    dispatch(productActions.getProductimgDownload(product_code));
  }, []);

  const imagePath = `/shopimg/${productImgDetail.product_img0}`;

  return (
    <div>
      <main>
        <div className="order-form">
          <section className="title">
            <h1>주문 및 결제</h1>
          </section>

          <div className="order-details">
            <div className="store-name">{productDetail.product_name}</div>
            <div className="order-info">
              <img
                className=""
                src={imagePath}
                alt="product_img0"
                width="300"
                height="300"
              />
              <p>가격: {productDetail.product_price}원</p>
              <label>
                수량 선택:
                <input
                  type="number"
                  name="quantity"
                  value={orderInfo.quantity}
                  onChange={handleQuantityChange}
                />
              </label>
              <p>
                총 결제 금액: {productDetail.product_price * orderInfo.quantity}
                원
              </p>
            </div>
          </div>

          <div className="order-menu">
            <h3>결제 수단 선택</h3>
            <div className="paymentMethod_box">
              <div className="paymentMethod_boxi">
                <img
                  className="paymenticorn"
                  src="/basicicon/card.png"
                  alt="카드"
                />
                <input
                  type="radio"
                  className="inputsize"
                  name="paymentMethod"
                  value="1"
                  checked={paymentMethod === "1"}
                  onChange={handlePaymentMethodChange}
                />
                신용카드
                <span className="menu-price"></span>
              </div>

              <div className="paymentMethod_boxi">
                <img
                  className="paymenticorn"
                  src="/basicicon/cash.png"
                  alt="카드"
                />
                <input
                  type="radio"
                  className="inputsize"
                  name="paymentMethod"
                  value="2"
                  checked={paymentMethod === "2"}
                  onChange={handlePaymentMethodChange}
                />
                계좌이체
                <span className="menu-price"></span>
              </div>
            </div>
          </div>

          <div className="total">
            <div>
              <span>총 결제 금액</span>
              <span>{productDetail.product_price * orderInfo.quantity}원</span>
            </div>
          </div>

          <button
            className="order-button"
            onClick={submitOrder}
            disabled={!paymentMethod}
          >
            {productDetail.product_price * orderInfo.quantity}원 결제하기
          </button>
        </div>
      </main>
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default PaymentPage;
