import axios from "axios";
import { chatRoomReducers } from "../reducers/chatReducer";

//친구추가
function getAddChatFriend(user_id2) {
  return async () => {
    await axios
      .post(`/api/chat/save`, user_id2)
      .then((response) => {
        const data = response.data;
        console.log(data);
        alert("친구추가");

        return data;
      })

      .catch((error) => {
        console.log(error);
        alert("친구추가실패");
      });
  };
}

//친구목록
function getFriendList(user_id1) {
  return async (dispatch) => {
    const data = await axios
      .get(`/api/chat/list/${user_id1}`)
      .then((Response) => Response.data);
    console.log("Map", data);

    dispatch(chatRoomReducers.getFriendList({ data }));
  };
}

export const chatActions = {
  getAddChatFriend,
  getFriendList,
};
