import React from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

const Search = () => {
  const navigate = useNavigate();
  const productImages = useSelector((state) => state.product.productImages);
  const productDetails = useSelector((state) => state.product.productDetails);

  const handleImgClick = (productCode) => {
    navigate(`/search/user/product/${productCode}`);
    console.log("---------", productCode);
  };

  return (
    <div className="product-list">
      {productDetails.map((detail) => {
        // 각 제품의 이미지 경로를 찾습니다. productImages에서 해당 제품 코드에 해당하는 이미지 경로를 찾거나, 기본 이미지 경로를 사용합니다.
        const imagePath = productImages[detail.product_code]
          ? `/shopimg/${productImages[detail.product_code]}`
          : "/path/to/default/image.png";

        return (
          <div key={detail.product_code} className="product-item">
            <img
              src={imagePath}
              alt={detail.product_name}
              width="300"
              height="300"
              onClick={() => handleImgClick(detail.product_code)}
              className="cursor-pointer"
            />
            <div>
              <div>{detail.product_name}</div>
              <div>{detail.product_price}</div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Search;
