// src/store/store.js
import { configureStore } from "@reduxjs/toolkit";
import cartReducer from "./cartSlice";
import paymentReducer from "./paymentSlice";

const store = configureStore({
  reducer: {
    cart: cartReducer,
    payment: paymentReducer,
  },
});

export default store;
