import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { chatActions } from "../../toolkit/actions/chat_Action";
import "./ChatHome.css";

const ChatHome = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [userCode, setUserCode] = useState({
    user_code1: "규연",
    user_code2: "",
  });

  const navichat = () => {
    navigate("/chat");
  };
  const navihome = () => {
    navigate("/");
  };

  const addFriend = (event) => {
    event.preventDefault(); // 폼 제출 시 페이지 리로드 방지
    dispatch(chatActions.getFriendInsert(userCode));

    setUserCode({
      user_code1: "규연",
      user_code2: "",
    });
  };

  useEffect(() => {}, []);

  return (
    <>
      <div className="chat">
        <div className="chat_header">
          <button onClick={navihome}>홈으로</button>
          <button onClick={navichat}>채팅리스트</button>
          <form onSubmit={addFriend}>
            <input
              type="text"
              value={userCode2}
              onChange={(e) => addFriend(e.target.value)}
              placeholder="친구 코드 입력"
            />
            <button type="submit">친구추가</button>
          </form>
        </div>
        <div className="chat_body">
          <div>홈</div>
        </div>
      </div>
    </>
  );
};

export default ChatHome;
