import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const API_URL = "http://localhost:8090";

const App = () => {
  const [product, setProduct] = useState({});
  const [orderInfo, setOrderInfo] = useState({
    deliveryAddress: "",
    deliveryInstruction: "",
    quantity: 1,
  });
  const [paymentResult, setPaymentResult] = useState("");

  useEffect(() => {
    // 상품 정보를 불러옵니다.
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/products/3`);
        setProduct(response.data);
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };
    fetchProduct();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderInfo((prev) => ({ ...prev, [name]: value }));
  };

  const handleQuantityChange = (e) => {
    setOrderInfo((prev) => ({
      ...prev,
      quantity: parseInt(e.target.value, 10) || 1,
    }));
  };

  const submitOrder = async () => {
    try {
      // 주문 정보를 서버에 제출합니다.
      const response = await axios.post(`${API_URL}/api/orders`, {
        deliveryAddress: orderInfo.deliveryAddress,
        deliveryInstruction: orderInfo.deliveryInstruction,
        productCode: product.product_code, // 서버가 기대하는 필드명을 확인하세요.
        quantity: orderInfo.quantity,
        orderAmount: product.product_price * orderInfo.quantity, // 총액 계산
      });

      if (response.status === 201) {
        setPaymentResult("주문이 성공적으로 처리되었습니다.");
      } else {
        setPaymentResult("주문 처리에 실패했습니다.");
      }
    } catch (error) {
      console.error("주문 처리 중 오류가 발생했습니다.", error);
      setPaymentResult("주문 처리 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="app">
      <h2>주문 및 결제</h2>
      <div>
        <input
          type="text"
          name="deliveryAddress"
          placeholder="배송지 주소"
          value={orderInfo.deliveryAddress}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="deliveryInstruction"
          placeholder="배송 요청 사항"
          value={orderInfo.deliveryInstruction}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="quantity"
          placeholder="수량"
          value={orderInfo.quantity}
          onChange={handleQuantityChange}
        />
      </div>
      {product && (
        <div className="product-info">
          <h3>상품 정보</h3>
          <p>제품명: {product.product_name}</p>
          <p>가격: {product.product_price}원</p>
          <p>수량: {orderInfo.quantity}</p>
          <p>총 금액: {product.product_price * orderInfo.quantity}원</p>
          <button onClick={submitOrder}>주문하기</button>
        </div>
      )}
      {paymentResult && <p>{paymentResult}</p>}
    </div>
  );
};

export default App;
