import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  // 상태 관리
  const [deliveryInfo, setDeliveryInfo] = useState({
    deliveryAddress: "",
    recipient: "",
    deliveryInstruction: "",
  });
  const [orderDetails, setOrderDetails] = useState({
    productId: 1, // 예시 ID, 실제 상품 ID로 대체 필요
    quantity: 1,
  });

  const [paymentMethod, setPaymentMethod] = useState(""); // 결제 수단 ID
  const [paymentResult, setPaymentResult] = useState(""); // 결제 결과 메시지

  // 사용자 입력 처리 함수
  const handleChange = (e) => {
    const { name, value } = e.target;
    setDeliveryInfo((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // 결제 처리 함수
  const handlePayment = async () => {
    try {
      // 주문 정보 백엔드로 전송
      const orderResponse = await axios.post("/api/orders", {
        ...deliveryInfo,
        ...orderDetails,
      });

      if (orderResponse.status === 201) {
        // 결제 요청
        const paymentResponse = await axios.post("/api/payments", {
          orderId: orderResponse.data.orderId,
          methodId: paymentMethod,
          amount: 26702, // 결제 금액, 실제 계산 로직 필요
        });

        if (paymentResponse.status === 200) {
          setPaymentResult("결제가 성공적으로 처리되었습니다.");
        }
      }
    } catch (error) {
      setPaymentResult("결제 처리 중 오류가 발생했습니다.");
      console.error("Payment error:", error);
    }
  };
  return (
    <div className="app">
      <div className="order-payment">
        <h2>Order / Payment</h2>
        <label htmlFor="delivery-address">배송 정보</label>
        <input type="text" id="delivery-address" placeholder="배송지" />
        <label htmlFor="recipient">배송지 변경</label>
        <input type="text" id="recipient" placeholder="배송지 변경" />

        <div>
          <span>이름 / 연락처</span>
          <span>신도일</span>
          <span>010-****-1213</span>
          <span>010-****-1213</span>
        </div>
        <div>
          <span>주소</span>
          <span>(06659) 서울 서초구 명달로로28길 20</span>
        </div>
        <div>
          <label htmlFor="delivery-instruction">배송 요청사항</label>
          <input
            type="text"
            id="delivery-instruction"
            placeholder="부재시 집 앞에 넣주세요"
          />
        </div>
      </div>

      <div className="order-details">
        <h3>토스간편결제 + 쿠폰</h3>
        <p>결제 금액: 26,702원</p>
        <table>
          <thead>
            <tr>
              <th>상품</th>
              <th>수량</th>
              <th>판매가</th>
              <th>배송</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>
                <img src="product-image.jpg" alt="Product" />
                <span>[토르시]</span>
                <span>데일리 무지 숏슬리브 - White</span>
                <span>옵션: 화이트/M</span>
              </td>
              <td>1개</td>
              <td>33,800원</td>
              <td>
                예상 발송일
                <br />
                (일)
              </td>
            </tr>
          </tbody>
          <tfoot>
            <tr>
              <td colSpan="2">상품 합계</td>
              <td>49,500원</td>
              <td></td>
            </tr>
            <tr>
              <td colSpan="2">배송비</td>
              <td>배송비 무료</td>
              <td></td>
            </tr>
            <tr>
              <td colSpan="2">상품 할인</td>
              <td>-15,700원</td>
              <td></td>
            </tr>
            <tr>
              <td colSpan="2">총 할인 금액</td>
              <td>-22,798원</td>
              <td></td>
            </tr>
            <tr>
              <td colSpan="2">최종 결제 금액</td>
              <td>26,702원</td>
              <td></td>
            </tr>
          </tfoot>
        </table>
      </div>

      <div className="payment-button">
        <button>26,702원 결제하기</button>
      </div>
    </div>
  );
};

export default App;
