import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { chatActions } from "../../toolkit/actions/chat_Action";
import "./Chat.css";

function Chat() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  /// 채팅리스트
  const [chatFriend, setChatFriend] = useState({
    chatroom_id: "",
    user_id1: 1,
    user_id2: "",
    chatroom_name: "",
  });

  const navichatlist = () => {
    navigate("/ws/chat", { state: { user_id: "1", chatRoomId: "2" } });
  };

  const { friendList = [] } = useSelector((state) => state.chat);

  /// 웹소켓
  const [webSocket, setWebSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isConnected, setIsConnected] = useState(false);
  const location = useLocation();
  const [chatRoom, setChatRoom] = useState(location.state?.user_id);
  // WebSocket 연결 설정

  //연결
  const connectWebSocket = () => {
    if (webSocket) {
      webSocket.close();
    }

    console.log(chatRoom);

    const ws = new WebSocket("ws://localhost:8090/ws/chat");

    ws.onopen = () => {
      console.log("WebSocket 서버에 연결됨");
      setIsConnected(true);
      const enterMessage = {
        messageType: "ENTER",
        chatRoomId: chatRoom,
        senderId: 1,
        message: "",
      };
      ws.send(JSON.stringify(enterMessage));
      console.log("입장", enterMessage);
    };

    ws.onmessage = (event) => {
      const message = JSON.parse(event.data);
      setMessages((prevMessages) => [...prevMessages, message]);
    };

    ws.onclose = (e) => {
      console.log(e.code);
      console.log("WebSocket 서버에서 연결 끊김");
      setIsConnected(false);
    };

    setWebSocket(ws);
  };

  useEffect(() => {}, []);

  const sendMessage = () => {
    if (webSocket) {
      const message = {
        messageType: "TALK",
        chatRoomId: chatRoom, // 채팅방에 따라 동적으로 설정해야 함
        senderId: 1, // 사용자의 ID여야 함
        message: inputMessage,
      };
      console.log(message);
      webSocket.send(JSON.stringify(message));
      setInputMessage("");
    }
  };

  const changeFriendName = (event) => {
    setChatRoom(event);
    retryConnection();
  };

  const retryConnection = () => {
    if (webSocket) {
      webSocket.close(); // 기존 연결 닫기
    }
    connectWebSocket(); // 새 연결 시작
  };

  const r = () => {
    if (webSocket) webSocket.close();
  };

  useEffect(() => {}, []);

  return (
    <div className="chat_home">
      {isConnected ? (
        <>
          <div className="chat_back_button" onClick={r}>
            뒤로
          </div>
          <h2>채팅방</h2>
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
          />
          <button onClick={sendMessage}>보내기</button>

          <div className="chat_content_box">
            {messages.map(
              (msg, index) =>
                msg.message && (
                  <p key={index}>
                    {msg.senderId}: {msg.message}
                  </p>
                )
            )}
          </div>
        </>
      ) : (
        <div>
          <div className="chat_home_header">
            <div>
              <input type="text" name="user_id2" />
            </div>
            <button type="submit">친구추가</button>
          </div>
          <div className="chat_home_body">
            <div className="chat_home_menubar">
              <button className="chm_button">친구</button>
              <button className="chm_button" onClick={navichatlist}>
                채팅
              </button>
            </div>
            <div>
              <div className="chatFriendList">
                {friendList
                  .filter((chat) => chat.user_id1 === chat.user_id2) // 조건에 맞는 항목만 필터링
                  .map((chat) => (
                    <div key={chat.chatroom_id} className="chatFriendListbox">
                      <div>
                        <div>나</div>
                      </div>
                    </div>
                  ))}
              </div>
              <div>
                {friendList &&
                  friendList
                    .filter((chat) => chat.user_id1 !== chat.user_id2)
                    .map((chat) => (
                      <div key={chat.chatroom_id} className="chatFriendListbox">
                        <div>구독방 넘버: {chat.chatroom_id}</div>
                        <div>구독방 친구: {chat.user_id2}</div>
                      </div>
                    ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chat;
