import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { chatActions } from "../../toolkit/actions/chat_Action";

const ChatHome = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [chatFriend, setChatFriend] = useState({
    chatroom_id: "",
    user_id1: 1,
    user_id2: "",
    chatroom_name: "",
  });

  const { friendList } = useSelector((state) => state.chat);

  const handleValueChange = (e) => {
    setChatFriend((prev) => {
      return { ...prev, [e.target.name]: e.target.value };
    });
  };

  const handleimgClick1 = () => {
    navigate("/ws/chat", { state: { user_id: "1", chatRoomId: "2" } });
  };

  const onSubmit = () => {
    console.log(chatActions.getAddChatFriend(chatFriend));
    dispatch(chatActions.getAddChatFriend(chatFriend));
  };

  useEffect(() => {
    console.log("로그인한 유저", chatFriend.user_id1);
    dispatch(chatActions.getFriendList(chatFriend.user_id1));
    console.log("친구리스트 받기", friendList);
  }, []);

  return (
    <>
      <div>
        <input type="text" name="user_id2" onChange={handleValueChange} />
      </div>
      <button type="submit" onClick={onSubmit}>
        친구추가
      </button>
      {/* 친구목록 */}
      <div>
        <div>친구목록</div>
        <div>
          {friendList &&
            friendList.map((chat) => (
              <div key={chat.chatroom_id} className="chatFriendList">
                <div>구독방 넘버: {chat.chatroom_id}</div>
                <div>구독방 친구: {chat.user_id2}</div>
              </div>
            ))}
        </div>
      </div>
    </>
  );
};

export default ChatHome;
