import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { getProductDetail } from "../../store/productSlice";
import { addToCart } from "../../store/cartSlice";

const ProductDetail = () => {
  const dispatch = useDispatch();
  const { user_id } = useParams();
  // const product = useSelector((state) => state.product.productDetail);
  const product = useSelector((state) => state.product);

  useEffect(() => {
    dispatch(getProductDetail(user_id));
  }, [dispatch, user_id]);

  const handleAddToCart = () => {
    dispatch(addToCart(product));
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="product-detail">
      <h2>{product.product_name}</h2>
      {/* <img src={product.product_img0} alt={product.product_name} /> */}
      <p>{product.product_count}</p>
      <p>가격: {product.product_price}원</p>
      <button onClick={handleAddToCart}>장바구니에 담기</button>
    </div>
  );
};

export default ProductDetail;
