import { useNavigate } from "react-router-dom";
import "./Productsave.css";
import { useState } from "react";
import axios from "axios";

const Porductsave = () => {
  const navigate = useNavigate();
  const [product, setProduct] = useState({
    seller_id: "",
    category_code: "",
    product_name: "",
    product_price: "",
    product_count: "",
  });

  const handleValueChange = (e) => {
    setProduct((prev) => {
      return { ...prev, [e.target.name]: e.target.value };
    });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await axios
      .post(`/product/save`, product)
      .then((response) => {
        navigate("/");
      })
      .catch((error) => {
        console.log(error);
      });
    alert("성공적으로 등록되었습니다");
  };

  return (
    <>
      <h1 className="head">물품등록</h1>
      <form className="save" onSubmit={onSubmit}>
        <tbody>
          <tr>
            <th>seller_id</th>
            <td>
              <input
                type="text"
                name="seller_id"
                onChange={handleValueChange}
              ></input>
            </td>
          </tr>
          <tr>
            <th>category_code</th>
            <td>
              <input
                type="text"
                name="category_code"
                onChange={handleValueChange}
              ></input>
            </td>
          </tr>
          <tr>
            <th>product_name</th>
            <td>
              <input
                type="text"
                name="product_name"
                onChange={handleValueChange}
              ></input>
            </td>
          </tr>
          <tr>
            <th>product_price</th>
            <td>
              <input
                type="text"
                name="product_price"
                onChange={handleValueChange}
              ></input>
            </td>
          </tr>
          <tr>
            <th>product_count</th>
            <td>
              <input
                type="text"
                name="product_count"
                onChange={handleValueChange}
              ></input>
            </td>
          </tr>
        </tbody>
        <button type="submit" className="btn btn-primary">
          등록완료
        </button>
      </form>
    </>
  );
};

export default Porductsave;
