import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  chatDetail: {},
  friendList: [],
  predictedLabels: [], // 예측 레이블 배열 추가
};

const chatSlice = createSlice({
  name: "chat",
  initialState,
  reducers: {
    getChat(state, action) {
      state.chatDetail = action.payload.data;
    },
    getFriendList(state, action) {
      state.friendList = action.payload.friendList;
    },
    getNLPsearch(state, action) {
      state.predictedLabels = action.payload.data; // 예측 레이블 배열 업데이트
    },
    setPredictedLabels(state, action) {
      state.predictedLabels = action.payload; // 예측 레이블 배열 설정
    },
  },
});

export const chatRoomReducers = chatSlice.actions;
export default chatSlice;
